import './App.css';
import Header from './MyComponents/Header';
import {Todos} from './MyComponents/Todos';
import {Footer} from './MyComponents/Footer';
import React, { useState } from 'react';

function App() {

  const onRemove = (todo)=>
  {
    console.log("You just removed ", todo);
    let i = todos.indexOf(todo);
    todos.splice(i, 1);
  }
  let todos = [
    {
      no: 1,
      title: "Project Completion in the morning",
      desc: "The Deadline is today"
    },
    {
      no: 2,
      title: "Important meeting in the afternoon",
      desc: "Big bussinessman coming for the meeting"
    },
    {
      no: 3,
      title: "Take the dog for a checkup",
      desc: "Appointment scheduled with the doctor"
    },
    {
      no: 4,
      title: "Eat fruits along with your lunch",
      desc: "Doctor's recommendation"
    }

  ]
  return (
    <>
    <Header title="My Todos List"searchBar={false}/>
    <Todos todos={todos} onRemove={onRemove}/>
    <Footer/>
    </>
  );
}

export default App;
